mapper.select:
SELECT user_id AS userId,
cargo_id AS cargoId,
cargo_type_id AS cargoTypeId,
container_id AS containerId,
gross
FROM shipper_cargo_table


        AND user_id = #{userId}


        AND cargo_id = #{cargoId}


        AND cargo_type_id = #{cargoTypeId}


        AND gross = #{gross}


        AND container_id = #{containerId}
